mapper.select:
SELECT *
FROM tb_non_product_manage



		AND equipment = #{equipment_name}



		AND action_date >=
		#{startDate}


		AND action_date <=
		#{endDate}


ORDER BY action_date DESC

SELECT *
FROM tb_tustest


		AND mch_name = #{mch_name}


		AND t_year = #{t_year}


ORDER BY id DESC

SELECT * FROM tb_incoming_test
AND page = #{page}
AND in_day LIKE CONCAT(#{in_day}, '%')

mapper.update:
UPDATE tb_incoming_test
in_day = #{in_day}, page = #{page}, supplier = #{supplier}, medicine = #{medicine}, lot = #{lot}, nv_1 = #{nv_1}, nv_2 = #{nv_2}, color_1 = #{color_1}, color_2 = #{color_2}, sensuality_1_1 = #{sensuality_1_1}, sensuality_2_1 = #{sensuality_2_1}, sensuality_1_2 = #{sensuality_1_2}, sensuality_2_2 = #{sensuality_2_2}, confirm = #{confirm}, file_name = #{file_name}, writer = #{writer}, confirmer = #{confirmer}, ph_1 = #{ph_1}, ph_2 = #{ph_2}, specific_gravity_1 = #{specific_gravity_1}, specific_gravity_2 = #{specific_gravity_2}, viscosity_1 = #{viscosity_1}, viscosity_2 = #{viscosity_2}, hardness_1 = #{hardness_1}, hardness_2 = #{hardness_2}, short_1 = #{short_1}, short_2 = #{short_2}, appearance_1 = #{appearance_1}, appearance_2 = #{appearance_2}, ex_1_1 = #{ex_1_1}, ex_1_2 = #{ex_1_2}, ex_2_1 = #{ex_2_1}, ex_2_2 = #{ex_2_2}, result = #{result}, update_id = #{update_id},
WHERE no = #{no}

UPDATE tb_medicine_standard
naoh_result = #{naoh_result}, hcl = #{hcl}, liter = #{liter}, naoh = #{naoh}, sc300a = #{sc300a}, sc300b = #{sc300b}, condense = #{condense}, after_naoh = #{after_naoh},
WHERE num = #{num}
