mapper.select:
SELECT *
FROM tb_standard_info


        AND plating_no = #{plating_no}


        AND pum_name = #{pum_name}


        AND surface_spec LIKE CONCAT('%',#{surface_spec},'%')


ORDER BY plating_no

SELECT *
FROM tb_corr_status


        AND equipment_name = #{equipment_name}



        AND replacement_date >= #{startDate}


        AND replacement_date <= #{endDate}


ORDER BY tdatetime DESC

SELECT * FROM tb_part_temp WHERE 1 = 1
AND `date` LIKE CONCAT(#{startDate}, '%')
AND mch_code = #{mch_code}

mapper.update:
UPDATE tb_part_temp
specific_gravity = #{value}, visocosity = #{value}, tank_temp = #{value}, chiller_temp = #{value},
WHERE id = #{id}
