mapper.select:
SELECT equipment_name, DATE_FORMAT(updated_at, '%Y-%m-%d') AS updated_at, item_type, m1, m2, m3, m4, m5, m6, m7, m8, m9, m10, m11, m12, save_url, remark FROM tb_check_plan
AND YEAR(created_at) = #{startDate}
ORDER BY equipment_name DESC

SELECT non_time_idx, equipment_name, machine_code, info_list, start_time, end_time, non_time_memo FROM tb_non_time
AND start_time >= #{startDate}
AND start_time <= #{endDate}
AND equipment_name = #{equipment_name}
ORDER BY start_time DESC

SELECT main.equipment_name AS v_equipment_name, main.info_list AS info_list_v,
-- 비가동 시간 계산 SUM( TIMESTAMPDIFF( MINUTE, STR_TO_DATE(main.start_time, '%Y-%m-%d %H:%i'), STR_TO_DATE(main.end_time, '%Y-%m-%d %H:%i') ) ) AS sum_time,
-- 시간:분 형식으로 변환 CONCAT( FLOOR(SUM( TIMESTAMPDIFF( MINUTE, STR_TO_DATE(main.start_time, '%Y-%m-%d %H:%i'), STR_TO_DATE(main.end_time, '%Y-%m-%d %H:%i') ) ) / 60), '시간 ', SUM( TIMESTAMPDIFF( MINUTE, STR_TO_DATE(main.start_time, '%Y-%m-%d %H:%i'), STR_TO_DATE(main.end_time, '%Y-%m-%d %H:%i') ) ) % 60, '분' ) AS sum_time_ch,
-- 하루 기준 최대 발생 건수 IFNULL(day_counts.max_day_count, 0) AS day_count,
-- 전체 발생 건수 COUNT(*) AS month_count
FROM tb_non_time main
LEFT JOIN ( SELECT equipment_name, info_list, MAX(day_count) AS max_day_count FROM ( SELECT equipment_name, info_list, DATE(STR_TO_DATE(start_time, '%Y-%m-%d %H:%i')) AS event_day, COUNT(*) AS day_count FROM tb_non_time WHERE 1 = 1
AND STR_TO_DATE(start_time, '%Y-%m-%d %H:%i') >= #{startDate}
AND STR_TO_DATE(end_time, '%Y-%m-%d %H:%i') <= #{endDate}
AND equipment_name = #{equipment_name}
GROUP BY equipment_name, info_list, event_day ) AS daily_counts GROUP BY equipment_name, info_list ) AS day_counts ON main.equipment_name = day_counts.equipment_name AND main.info_list = day_counts.info_list
WHERE 1 = 1
AND STR_TO_DATE(main.start_time, '%Y-%m-%d %H:%i') >= #{startDate}
AND STR_TO_DATE(main.end_time, '%Y-%m-%d %H:%i') <= #{endDate}
AND main.equipment_name = #{equipment_name}
GROUP BY main.equipment_name, main.info_list
ORDER BY month_count DESC

SELECT * FROM (
    SELECT *
    FROM tb_repair_status
    WHERE mch_name = #{mch_name}

    UNION ALL

    SELECT *
    FROM tb_repair_status
    WHERE NOT EXISTS (
        SELECT 1 FROM tb_repair_status WHERE mch_name = #{mch_name}
    )
    LIMIT 100
) AS sub
ORDER BY insert_date DESC

SELECT *
FROM (
  SELECT * 
  FROM tb_part_status 
  WHERE mch_name = #{mch_name}

  UNION ALL

  SELECT * 
  FROM tb_part_status 
  WHERE NOT EXISTS (
    SELECT 1 FROM tb_part_status WHERE mch_name = #{mch_name}
  )
) AS sub
ORDER BY no DESC

SELECT *
FROM (
    SELECT *
    FROM tb_sparestatus_status
    WHERE mch_name = #{mch_name}

    UNION ALL

    SELECT *
    FROM tb_sparestatus_status
    WHERE NOT EXISTS (
        SELECT 1 FROM tb_sparestatus_status WHERE mch_name = #{mch_name}
    )
) AS sub
ORDER BY no DESC

SELECT *
FROM tb_log_status
WHERE LEFT(gong_date,4) = #{gong_date}

  AND mch_name = #{mch_name}

ORDER BY gong_date DESC

SELECT e.position_cd, e.mach_code, e.facility_name, e.line_cd, e.err_code, e.err_name, e.start_time_formatted, e.end_time_formatted, e.remark FROM v_errdata e WHERE 1 = 1
AND e.mach_code = #{mach_code}
AND e.start_time_formatted >= #{start_time} AND e.end_time_formatted <= #{end_time} ORDER BY e.start_time_formatted
