mapper.update:
UPDATE tb_medication_situation
SET
  `date` = #{date},
  `status` = #{status},
  `next_month` = #{next_month},
  `stock_cnt` = #{stock_cnt},


      geomet_g1 = #{field_name}


      geomet_g2 = #{field_name}


      geomet_adding = #{field_name}


      pluse = #{field_name}


      ml_h = #{field_name}


      ml_g = #{field_name}


      k_black = #{field_name}


      naoh_99 = #{field_name}


      sc_300a = #{field_name}


      sc330b_3x = #{field_name}


      sc330_liquid = #{field_name}


      geomet_sus = #{field_name}


      ed2800_a_black = #{field_name}


      ed2800_b = #{field_name}


      geomet_005 = #{field_name}


      geomet_069 = #{field_name}


      geomet_p_210 = #{field_name}


      geomet_sq_70 = #{field_name}


WHERE id = #{id}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY end_time) AS r_num, start_time, end_time, tong_day, weight_day, item_nm, item_cd, next_facility, '' AS a, '' AS b, '' AS c, '' AS d, '' AS e, '' AS f FROM ( SELECT *, LEAD(facility_name) OVER(PARTITION BY barcode_no ORDER BY start_time) AS next_facility FROM ( SELECT mach_code, barcode_no, weight AS weight_day, 1 AS tong_day, fac.facility_name, start_time, end_time, item_cd, item_nm, LEAD(start_time) OVER(PARTITION BY mach_code, barcode_no ORDER BY start_time DESC) AS next_start_time FROM tb_lowdata_save AS lows INNER JOIN tb_facility AS fac ON lows.mach_code = fac.facility_mach_code WHERE 1=1
AND end_time >= #{s_time}
AND end_time <= #{e_time}
AND mach_code = #{m_code}
AND weight IS NOT NULL AND start_time IS NOT NULL AND end_time IS NOT NULL ) AS aa ) AS bb
mach_code = #{m_code}
ORDER BY end_time

SELECT mach_code, tong_day, (tong_day * std_weight) AS weight_day, tong_sum, (tong_sum * std_weight) AS weight_sum, work_time, work_time / 24 AS work_percent, (diff_day * 24) AS sum_time, (diff_day * 20) / (diff_day * 24) AS sum_percent, (tong_day * std_weight) / tong_day AS avg_day, (tong_sum * std_weight) / tong_sum AS avg_sum, work_time / tong_day AS uph, (diff_day * 24) / tong_sum AS uph_sum FROM ( SELECT mach_code, SUM(CASE WHEN end_time >= #{s_time} AND end_time <= #{e_time} THEN 1 ELSE 0 END) AS tong_day, SUM(CASE WHEN end_time >= #{s_time} AND end_time <= #{e_time} THEN weight ELSE 0 END) AS weight_day, SUM(1) AS tong_sum, SUM(weight) AS weight_sum, MAX(CASE WHEN #{s_time} <= end_time AND end_time <= #{e_time} THEN CASE WHEN TIMESTAMPDIFF( HOUR, STR_TO_DATE(DATE_FORMAT(NOW(), '%Y%m%d080000'), '%Y%m%d%H%i%s'), NOW() ) = 0 THEN 1 ELSE TIMESTAMPDIFF( HOUR, STR_TO_DATE(DATE_FORMAT(NOW(), '%Y%m%d080000'), '%Y%m%d%H%i%s'), NOW() ) END ELSE 0 END) AS work_time, TIMESTAMPDIFF( DAY, MIN(end_time), MAX(end_time) ) AS diff_day, MAX(std_weight) AS std_weight FROM tb_lowdata_save lows INNER JOIN tb_facility fac ON lows.mach_code = fac.facility_mach_code WHERE 1=1
AND mach_code = #{m_code}
AND end_time >= #{s_time}
AND end_time <= #{e_time}
AND weight IS NOT NULL AND start_time IS NOT NULL AND end_time IS NOT NULL GROUP BY mach_code ) AS agg

SELECT idx, input_date, mch_code, mch_name, gb, visc, pre_temp, heat_temp, liq_temp, sg FROM tb_report_input WHERE 1=1
AND input_date BETWEEN #{s_time} AND #{e_time}
AND mch_code = #{m_code}
